<template>
  <div>
    <Header />
    <router-view
      :products="products"
      :cart="cart"
      @add-to-cart="addToCart"
      @remove-from-cart="removeFromCart"
      @clear-cart="clearCart"
    />
  </div>
</template>

<script>
import Header from './components/Header.vue';

export default {
  name: 'App',
  components: { Header },
  data() {
    return {
      products: [
        {
          id: 1,
          name: 'Кроссовки',
          price: 5000,
          available: true,
          image: 'data:image/jpeg;base64,',
        },
        {
          id: 2,
          name: 'Футболка',
          price: 1000,
          category: 'Одежда',
          image: 'data:image/jpeg;base64,',
        },
        {
          id: 3,
          name: 'Часы',
          price: 7000,
          category: 'Аксессуары',
          image: 'data:image/jpeg;base64,',
        },
        {
          id: 4,
          name: 'Бейсболка',
          price: 1500,
          category: 'Аксессуары',
          image: 'data:image/jpeg;base64,',
        },
        {
          id: 5,
          name: 'Джинсы',
          price: 3000,
          category: 'Одежда',
          image: 'data:image/jpeg;base64,',
        },
        // Добавьте больше товаров по необходимости
      ],
      cart: [],
    };
  },
  methods: {
    addToCart(product) {
      const existingProduct = this.cart.find(item => item.id === product.id);
      if (existingProduct) {
        existingProduct.quantity++;
      } else {
        this.cart.push({ ...product, quantity: 1 });
      }
    },
    removeFromCart(id) {
      this.cart = this.cart.filter(item => item.id !== id);
    },
    clearCart() {
      this.cart = [];
    },
  },
};
</script>

